const playBtn = document.getElementById("open-video");
const modal = document.getElementById("video-modal");
const closeBtn = document.getElementById("close-video");
const iframe = document.getElementById("youtube-video");

playBtn.addEventListener("click", function(e) {
    e.preventDefault();
    iframe.src = "https://www.youtube.com/embed/Get7rqXYrbQ?autoplay=1&controls=0&rel=0&modestbranding=1"; // Kontrolsüz video
    modal.style.display = "flex";
});

closeBtn.addEventListener("click", function() {
    iframe.src = ""; // Video durdur
    modal.style.display = "none";
});

// Modal dışında tıklayınca da kapat
modal.addEventListener("click", function(e){
    if(e.target === modal){
        iframe.src = "";
        modal.style.display = "none";
    }
});